import React from 'react';
import { Link } from 'react-router-dom';

class Login extends React.Component {
  constructor() {
    super();

    this.state = {
      email: '',
      password: '',
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  handleChange({ target: { name, value } }) {
    this.setState({ [name]: value });
  }

  handleClick() {
  }

  render() {
    const { email, password } = this.state;
    const validation = true;
    const MIN_PASSWORD_LENGTH = 6;

    // Para validação de email adaptada do seguinte link: https://pt.stackoverflow.com/questions/1386/express%C3%A3o-regular-para-valida%C3%A7%C3%A3o-de-e-mail
    const EMAIL_REGEX = /^[a-z0-9.]+@[a-z0-9]+\.[a-z]+?$/i;
    const emailValidation = EMAIL_REGEX.test(email);
    if (emailValidation && password.length >= MIN_PASSWORD_LENGTH) {
      validation = false;
    }

    return (
      <div>
        <input
          type="email"
          data-testid="email-input"
          placeholder="Insira o email"
          id="email"
          onChange={ this.handleChange }
        />
        <input
          type="text"
          data-testid="password-input"
          placeholder="Insira a senha"
          id="password"
          minLength="6"
          onChange={ this.handleChange }
        />
        <Link to="carteira">
          <button
            type="button"
            disabled={ validation }
            onClick={ this.handleClick }
          >
            Entrar
          </button>
        </Link>
      </div>
    );
  }
}

export default Login;
